mapper.select:
SELECT COUNT(*) FROM ADMINHISTORY
WHERE U_ID = #{uId}
ORDER BY 1

		select b.* from ( select a.*, rownum rn from (
		SELECT *
		FROM ADMINHISTORY
		
			WHERE U_ID = #{uId}
		
		ORDER BY 1
		) a ) b where rn between #{first} and #{last}

SELECT SUBSTR(C.AHDATE,9,2) AS DAY, OLD_MILEAGE, NEW_MILEAGE, (NEW_MILEAGE-OLD_MILEAGE) AS CNTMILEAGE FROM (SELECT TO_CHAR(A.AH_DATE,'yyyy/mm/dd') AS AHDATE, A.OLD_MILEAGE FROM ADMINHISTORY A, (SELECT MIN(NUM) AS MINNUM, TO_CHAR(AH_DATE,'yyyy/mm/dd') FROM ADMINHISTORY WHERE U_ID ='admin' GROUP BY TO_CHAR(AH_DATE,'yyyy/mm/dd')) B WHERE A.NUM = B.MINNUM) C JOIN (SELECT TO_CHAR(A.AH_DATE,'yyyy/mm/dd') AS AHDATE, A.NEW_MILEAGE FROM ADMINHISTORY A, (SELECT MAX(NUM) AS MAXNUM, TO_CHAR(AH_DATE,'yyyy/mm/dd') FROM ADMINHISTORY WHERE U_ID ='admin' GROUP BY TO_CHAR(AH_DATE,'yyyy/mm/dd')) B WHERE A.NUM = B.MAXNUM) D ON C.AHDATE = D.AHDATE
WHERE SUBSTR(C.AHDATE,3,2) = #{year} AND SUBSTR(C.AHDATE,6,2) = #{month}
ORDER BY 1
