mapper.select:
select b.* from ( select a.*, rownum rn from ( SELECT * FROM (SELECT PURCHASE_NUM, U_ID, P.GAME_ID, BETMONEY, BETDRATE, ACCURACY, BETDATE, CHOISE, REDTEAMNAME vsTeam, RETURNCHECK FROM PURCHASE P JOIN INGAME I ON P.GAME_ID = I.GAME_ID AND P.CHOISE = I.BLUETEAMNAME UNION ALL SELECT PURCHASE_NUM, U_ID, P.GAME_ID, BETMONEY, BETDRATE, ACCURACY, BETDATE, CHOISE, BLUETEAMNAME vsTeam, RETURNCHECK FROM PURCHASE P JOIN INGAME I ON P.GAME_ID = I.GAME_ID AND P.CHOISE = I.REDTEAMNAME) WHERE 1 = 1
AND ${searchCondition} like #{searchKeyword}
ORDER BY 1 ) a ) b where rn between #{first} and #{last}

select substr(betdate,7,2) as day, count(*) as cnt from purchase

where substr(betdate,1,2) = #{year} AND substr(betdate,4,2) = #{month}

group by substr(betdate,7,2)
