mapper.select:
SELECT REDTEAMNAME vsTeam, COUNT(BLUERESULT) playerMatch, SUM(CASE WHEN BLUERESULT = 'win' THEN '1' ELSE '0' END) win, COUNT(CASE WHEN BLUERESULT = 'lose' THEN '1' ELSE NULL END) lose FROM (SELECT MATCHDATE, MATCHNAME, MATCHINFO, PLAYTIME, BLUETEAMNAME, REDTEAMNAME, BLUERESULT, REDRESULT FROM INGAME WHERE BLUETEAMNAME = #{teamId}
UNION ALL SELECT MATCHDATE, MATCHNAME, MATCHINFO, PLAYTIME, REDTEAMNAME, BLUETEAMNAME, REDRESULT, BLUERESULT FROM INGAME WHERE REDTEAMNAME = #{teamId})
GROUP BY REDTEAMNAME

mapper.insert:
INSERT INTO TEAM(TEAM_ID, DIRECTOR, HEAD_COACH, NAME
, FILENAME
) VALUES (#{teamId}, #{director}, #{headCoach}, #{name}
, #{fileName}
)

mapper.update:
UPDATE	TEAM
SET		TEAM_ID = #{teamId},
		DIRECTOR = #{director},
		HEAD_COACH = #{headCoach},
		NAME = #{name}
		
	
		,
		FILENAME = #{fileName}
	
WHERE	TEAM_ID = #{teamId}
